import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  initializeBoard, 
  isValidMove, 
  isCheck, 
  checkGameEnd, 
  COLORS 
} from '../utils/chessRules';
import { getBestMove } from '../utils/chessAI';
import ChessBoard from '../components/ChessBoard';
import './Game.css';

const AIGame = () => {
  const [board, setBoard] = useState(null);
  const [selectedCell, setSelectedCell] = useState(null);
  const [validMoves, setValidMoves] = useState([]);
  const [currentTurn, setCurrentTurn] = useState(COLORS.RED); // 红方先行
  const [gameStatus, setGameStatus] = useState('playing');
  const [winner, setWinner] = useState(null);
  const [isCheckState, setIsCheckState] = useState(false);
  const [difficulty, setDifficulty] = useState(3); // 默认难度级别为3
  const [playerColor, setPlayerColor] = useState(COLORS.RED); // 默认玩家执红
  const [moveHistory, setMoveHistory] = useState([]);

  const navigate = useNavigate();

  // 初始化棋盘
  useEffect(() => {
    console.log('初始化棋盘useEffect触发, 玩家颜色:', playerColor);
    resetGame();
    // 只在组件挂载和玩家颜色改变时重置游戏，而不是在其他状态改变时
  }, [playerColor]);

  // 调试当前回合和玩家颜色的变化
  useEffect(() => {
    console.log('回合/玩家颜色状态更新 - 当前回合:', currentTurn, '玩家颜色:', playerColor, '是否为玩家回合:', currentTurn === playerColor);
  }, [currentTurn, playerColor]);

  // 重置游戏
  const resetGame = () => {
    console.log('重置游戏，玩家颜色:', playerColor);
    const newBoard = initializeBoard();
    console.log('初始化新棋盘');

    // 重置所有状态
    setBoard(newBoard);
    setSelectedCell(null);
    setValidMoves([]);
    setCurrentTurn(COLORS.RED);  // 红方永远先行
    setGameStatus('playing');
    setWinner(null);
    setIsCheckState(false);
    setMoveHistory([]);

    console.log('游戏状态重置完成');

    // 如果AI是红方且先行，立即执行AI移动
    if (playerColor === COLORS.BLACK) {
      console.log('AI执红先行，准备AI移动');
      // 使用setTimeout确保状态已经更新
      setTimeout(() => {
        // 重要：确保当前状态为最新
        if (playerColor === COLORS.BLACK && currentTurn === COLORS.RED) {
          console.log('执行AI首步移动，当前回合:', COLORS.RED, '玩家颜色:', playerColor);
          // 直接使用最新创建的棋盘，而不是依赖state中的board
          makeAIMove(newBoard);
        } else {
          console.log('状态不匹配，AI不执行首步移动:', {
            玩家颜色: playerColor,
            当前回合: currentTurn
          });
        }
      }, 500);
    } else {
      console.log('玩家执红先行');
    }
  };

  // 当前是否轮到玩家行动
  const isPlayerTurn = currentTurn === playerColor;

  // 格子点击处理
  const handleCellClick = (row, col) => {
    try {
      console.log('点击格子:', row, col, '当前回合:', currentTurn, '是否玩家回合:', isPlayerTurn);

      // 验证坐标
      if (row < 0 || row >= 10 || col < 0 || col >= 9) {
        console.error('无效的格子坐标:', row, col);
        return;
      }

      // 游戏结束或非玩家回合时不允许操作
      if (gameStatus !== 'playing' || !isPlayerTurn) {
        if (gameStatus !== 'playing') {
          console.log('游戏已结束，无法移动');
        } else {
          console.log('当前不是您的回合，玩家颜色:', playerColor, '当前回合:', currentTurn);
        }
        return;
      }

      const clickedPiece = board[row][col];
      console.log('点击的棋子:', clickedPiece);

      // 如果已选中棋子，尝试移动
      if (selectedCell) {
        const { row: selectedRow, col: selectedCol } = selectedCell;
        console.log('当前已选中棋子:', selectedRow, selectedCol);

        // 验证选中的格子是否有效
        if (selectedRow < 0 || selectedRow >= 10 || selectedCol < 0 || selectedCol >= 9) {
          console.error('无效的已选中格子:', selectedCell);
          setSelectedCell(null);
          setValidMoves([]);
          return;
        }

        const selectedPiece = board[selectedRow][selectedCol];
        console.log('已选中的棋子:', selectedPiece);

        // 点击同一个棋子，取消选择
        if (selectedRow === row && selectedCol === col) {
          console.log('取消选择当前棋子');
          setSelectedCell(null);
          setValidMoves([]);
          return;
        }

        // 检查是否是有效移动
        const isValid = validMoves.some(move => move.row === row && move.col === col);
        console.log('是否有效移动:', isValid, '有效移动列表:', validMoves);

        if (
          selectedPiece && 
          selectedPiece.color === currentTurn &&
          isValid
        ) {
          console.log('执行有效移动');
          // 执行移动
          makeMove(selectedRow, selectedCol, row, col);
        } else if (clickedPiece && clickedPiece.color === currentTurn) {
          console.log('选择新的己方棋子');
          // 选择新的己方棋子
          selectPiece(row, col);
        } else {
          console.log('无效点击，取消选择');
          // 无效点击，取消选择
          setSelectedCell(null);
          setValidMoves([]);
        }
      } 
      // 如果未选中棋子，选择一个棋子
      else if (clickedPiece && clickedPiece.color === currentTurn) {
        console.log('选择己方棋子');
        selectPiece(row, col);
      } else if (clickedPiece) {
        console.log('不能选择对方的棋子');
      } else {
        console.log('此处没有棋子');
      }
    } catch (error) {
      console.error('点击处理出错:', error);
      // 重置选择状态
      setSelectedCell(null);
      setValidMoves([]);
    }
  };

  // 选择棋子，计算有效移动
  const selectPiece = (row, col) => {
    try {
      console.log('选择棋子:', row, col);

      // 验证坐标
      if (row < 0 || row >= 10 || col < 0 || col >= 9) {
        console.error('无效的棋子坐标:', row, col);
        return;
      }

      // 验证有棋子
      const piece = board[row][col];
      if (!piece) {
        console.error('该位置没有棋子:', row, col);
        return;
      }

      // 验证是当前回合的棋子
      if (piece.color !== currentTurn) {
        console.error('不是当前回合的棋子:', piece, '当前回合:', currentTurn);
        return;
      }

      setSelectedCell({ row, col });
      console.log('已选中棋子:', { row, col }, piece);

      // 计算有效移动
      const newValidMoves = [];

      for (let toRow = 0; toRow < 10; toRow++) {
        for (let toCol = 0; toCol < 9; toCol++) {
          try {
            if (isValidMove(piece, board, row, col, toRow, toCol)) {
              // 模拟移动，检查是否会导致自己被将军
              try {
                const newBoard = JSON.parse(JSON.stringify(board));
                newBoard[toRow][toCol] = newBoard[row][col];
                newBoard[row][col] = null;

                if (!isCheck(newBoard, piece.color)) {
                  newValidMoves.push({ row: toRow, col: toCol });
                }
              } catch (checkError) {
                console.error('检查将军状态时出错:', checkError, toRow, toCol);
                // 跳过这个移动
                continue;
              }
            }
          } catch (moveError) {
            console.error('检查有效移动时出错:', moveError, toRow, toCol);
            // 跳过这个移动
            continue;
          }
        }
      }

      console.log('计算有效移动数量:', newValidMoves.length, newValidMoves);

      // 如果没有有效移动，提示用户
      if (newValidMoves.length === 0) {
        console.log('没有有效移动可用');
      }

      setValidMoves(newValidMoves);
    } catch (error) {
      console.error('选择棋子出错:', error);
      setSelectedCell(null);
      setValidMoves([]);
    }
  };

  // 执行移动
  const makeMove = (fromRow, fromCol, toRow, toCol) => {
    try {
      console.log('执行移动:', fromRow, fromCol, toRow, toCol, '当前回合:', currentTurn);

      // 验证坐标
      if (
        fromRow < 0 || fromRow >= 10 || fromCol < 0 || fromCol >= 9 ||
        toRow < 0 || toRow >= 10 || toCol < 0 || toCol >= 9
      ) {
        console.error('无效的移动坐标:', fromRow, fromCol, toRow, toCol);
        return;
      }

      // 验证起始位置有棋子
      if (!board[fromRow] || !board[fromRow][fromCol]) {
        console.error('起始位置没有棋子:', fromRow, fromCol);
        return;
      }

      // 验证是当前回合的棋子
      const piece = board[fromRow][fromCol];
      if (!piece || piece.color !== currentTurn) {
        console.error('不是当前回合的棋子:', piece, '当前回合:', currentTurn);
        return;
      }

      // 创建新棋盘（深拷贝）
      const newBoard = JSON.parse(JSON.stringify(board));

      // 记录移动历史
      const historyEntry = {
        fromRow,
        fromCol,
        toRow,
        toCol,
        piece: board[fromRow][fromCol],
        captured: board[toRow][toCol],
        board: JSON.parse(JSON.stringify(board))
      };
      console.log('记录移动历史:', historyEntry);
      setMoveHistory(prev => [...prev, historyEntry]);

      // 执行移动
      newBoard[toRow][toCol] = newBoard[fromRow][fromCol];
      newBoard[fromRow][fromCol] = null;

      // 更新棋盘状态
      console.log('更新棋盘状态');
      setBoard(newBoard);
      setSelectedCell(null);
      setValidMoves([]);

      // 切换回合
      const nextTurn = currentTurn === COLORS.RED ? COLORS.BLACK : COLORS.RED;
      console.log('切换回合:', currentTurn, '->', nextTurn);
      setCurrentTurn(nextTurn);

      // 检查是否将军
      try {
        const isInCheck = isCheck(newBoard, nextTurn);
        console.log('是否将军:', isInCheck, '下一回合:', nextTurn);
        setIsCheckState(isInCheck);
      } catch (checkError) {
        console.error('检查是否将军时出错:', checkError);
      }

      // 检查游戏是否结束
      try {
        const gameEndResult = checkGameEnd(newBoard, currentTurn);
        console.log('游戏是否结束:', gameEndResult);

        if (gameEndResult.isGameOver) {
          console.log('游戏结束，胜者:', gameEndResult.winner);
          setGameStatus('over');
          setWinner(gameEndResult.winner);
          return; // 游戏结束，不再执行AI移动
        } else {
          // 如果下一回合是AI的回合，让AI行动
          if (nextTurn !== playerColor) {
            console.log('准备执行AI移动，延迟500ms');
            setTimeout(() => {
              console.log('开始执行AI移动');
              makeAIMove(newBoard);
            }, 500);
          } else {
            console.log('下一回合是玩家的回合:', playerColor);
          }
        }
      } catch (gameEndError) {
        console.error('检查游戏结束时出错:', gameEndError);
        // 即使检查游戏结束出错，也继续游戏
        if (nextTurn !== playerColor) {
          console.log('发生错误，仍然执行AI移动');
          setTimeout(() => makeAIMove(newBoard), 500);
        }
      }
    } catch (error) {
      console.error('执行移动时出错:', error);
    }
  };

  // AI执行移动
  const makeAIMove = (currentBoard) => {
    const aiColor = playerColor === COLORS.RED ? COLORS.BLACK : COLORS.RED;

    try {
      console.log('AI回合开始，AI颜色:', aiColor, '难度级别:', difficulty);
      console.log('当前棋盘状态:', JSON.stringify(currentBoard));

      // 验证棋盘数据是否有效
      if (!currentBoard || !Array.isArray(currentBoard) || currentBoard.length !== 10) {
        console.error('无效的棋盘数据传递给AI:', currentBoard);
        return;
      }

      // 确保当前回合是AI的回合
      if (currentTurn !== aiColor) {
        console.error('当前不是AI的回合，无法执行AI移动, 当前回合:', currentTurn, 'AI颜色:', aiColor);
        return;
      }

      console.log('AI正在计算最佳移动...');
      // 添加更多的错误处理和调试信息
      try {
        const move = getBestMove(currentBoard, aiColor, difficulty);
        console.log('AI计算结果:', move);

        if (move) {
          console.log('AI执行移动:', move);
          makeMove(move.fromRow, move.fromCol, move.toRow, move.toCol);
        } else {
          // AI无法找到合法移动，可能被将军
          console.log('AI无法找到合法移动');

          // 检查游戏是否结束
          try {
            const { isGameOver, winner } = checkGameEnd(currentBoard, playerColor);
            console.log('游戏结束检查:', isGameOver, winner);
            if (isGameOver) {
              setGameStatus('over');
              setWinner(winner);
            }
          } catch (gameEndError) {
            console.error('检查游戏结束时出错:', gameEndError);
          }
        }
      } catch (aiError) {
        console.error('调用getBestMove出错:', aiError);

        // 尝试回退到随机移动作为备用方案
        try {
          console.log('尝试进行随机移动...');
          const moves = [];

          // 收集AI所有可能的有效移动
          for (let fromRow = 0; fromRow < 10; fromRow++) {
            for (let fromCol = 0; fromCol < 9; fromCol++) {
              const piece = currentBoard[fromRow][fromCol];
              if (piece && piece.color === aiColor) {
                for (let toRow = 0; toRow < 10; toRow++) {
                  for (let toCol = 0; toCol < 9; toCol++) {
                    if (isValidMove(piece, currentBoard, fromRow, fromCol, toRow, toCol)) {
                      // 检查移动后是否会导致自己被将军
                      const tempBoard = JSON.parse(JSON.stringify(currentBoard));
                      tempBoard[toRow][toCol] = tempBoard[fromRow][fromCol];
                      tempBoard[fromRow][fromCol] = null;

                      if (!isCheck(tempBoard, aiColor)) {
                        moves.push({ fromRow, fromCol, toRow, toCol });
                      }
                    }
                  }
                }
              }
            }
          }

          console.log('找到随机移动数量:', moves.length);

          if (moves.length > 0) {
            const randomMove = moves[Math.floor(Math.random() * moves.length)];
            console.log('执行随机移动:', randomMove);
            makeMove(randomMove.fromRow, randomMove.fromCol, randomMove.toRow, randomMove.toCol);
          } else {
            console.log('找不到任何合法移动，游戏可能已结束');
            setGameStatus('over');
            setWinner(playerColor);
          }
        } catch (randomMoveError) {
          console.error('尝试随机移动失败:', randomMoveError);
        }
      }
    } catch (error) {
      console.error('AI移动出错:', error);
    }
  };

  // 悔棋
  const handleUndo = () => {
    if (moveHistory.length < 2 || gameStatus !== 'playing') return;

    // 需要撤销两步（玩家和AI的移动）
    const previousState = moveHistory[moveHistory.length - 2];
    setBoard(previousState.board);
    setMoveHistory(moveHistory.slice(0, -2));
    setSelectedCell(null);
    setValidMoves([]);
    setCurrentTurn(playerColor);
    setIsCheckState(false);
  };

  // 切换玩家颜色
  const togglePlayerColor = () => {
    setPlayerColor(playerColor === COLORS.RED ? COLORS.BLACK : COLORS.RED);
  };

  // 修改AI难度
  const changeDifficulty = (newDifficulty) => {
    setDifficulty(newDifficulty);
  };

  // 返回主页
  const goToHome = () => {
    navigate('/');
  };

  if (!board) return <div>加载中...</div>;

  return (
    <div className="game-container">
      <div className="game-info">
        <h1>人机对战</h1>
        <div className="status-panel">
          <p>当前回合: <span className={currentTurn === COLORS.RED ? 'red-text' : 'black-text'}>
            {currentTurn === COLORS.RED ? '红方' : '黑方'}
          </span></p>
          {isCheckState && <p className="check-warning">将军！</p>}
          {gameStatus === 'over' && (
            <div className="game-result">
              <p>游戏结束！</p>
              <p>{winner === COLORS.RED ? '红方' : '黑方'}获胜</p>
            </div>
          )}
        </div>

        <div className="control-panel">
          <div className="difficulty-control">
            <label>AI难度：</label>
            <select 
              value={difficulty} 
              onChange={(e) => changeDifficulty(Number(e.target.value))}
              disabled={gameStatus === 'playing' && moveHistory.length > 0}
            >
              <option value={1}>简单</option>
              <option value={2}>中等</option>
              <option value={3}>困难</option>
              <option value={4}>专家</option>
              <option value={5}>大师</option>
            </select>
          </div>

          <div className="color-control">
            <label>执子：</label>
            <select 
              value={playerColor} 
              onChange={() => togglePlayerColor()}
              disabled={gameStatus === 'playing' && moveHistory.length > 0}
            >
              <option value={COLORS.RED}>红方</option>
              <option value={COLORS.BLACK}>黑方</option>
            </select>
          </div>

          <button 
            className="btn secondary-btn" 
            onClick={handleUndo}
            disabled={moveHistory.length < 2 || gameStatus !== 'playing'}
          >
            悔棋
          </button>

          <button 
            className="btn primary-btn" 
            onClick={resetGame}
          >
            重新开始
          </button>

          <button 
            className="btn secondary-btn" 
            onClick={goToHome}
          >
            返回主页
          </button>
        </div>
      </div>

      <div className="board-container">
        <ChessBoard 
          board={board}
          selectedCell={selectedCell}
          validMoves={validMoves}
          onCellClick={handleCellClick}
          reversed={playerColor === COLORS.BLACK}
        />
      </div>
    </div>
  );
};

export default AIGame;
